import { createSlice } from "@reduxjs/toolkit";

const initialState={
posts:[
    {
        title:'about jai',
        author:'jai',
        userid:0,
        tags:['bio','resume'],
        typography:'i am a developer of my own world',
        date:'2-07-2022'
    }],
users:[{
    username:'jai',
    mobile:8756875687,
    password:'112233'
}],
sessionIndex:0
}


const DataSlice:any=createSlice({
    name:'DataSlice',
    initialState,
    reducers:{
    addBlog:(state,action)=>{
        state.posts.push( {
            title: action.payload.title,
            author:'jai',
            userid:0,
            tags:['bio','resume'],
            typography:'i am a developer of my own world',
            date:'2-07-2022'
        })
    }
    }
})

export default DataSlice.reducer;
export const { addBlog} = DataSlice.actions
